import React, { useState, useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { Container, TextField, Button, Grid } from '@mui/material';
import GoogleLogin from 'react-google-login';
import FacebookLogin from 'react-facebook-login';
import { gapi } from 'gapi-script';
import './LoginScreen.css'; // Import your CSS file for styling

const LoginScreen = () => {
  const history = useHistory();
  const [userLocation, setUserLocation] = useState(null); // State for storing user location

  const clientId = '342699852295-18ukre2mehesbtmd2rjq82id2bhpqnob.apps.googleusercontent.com';

  useEffect(() => {
    gapi.load('auth2', () => {
      gapi.auth2.init({ client_id: clientId });
    });

    // Request user location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setUserLocation({ latitude, longitude });
        },
        (error) => {
          console.error('Error getting location:', error);
        }
      );
    } else {
      console.error('Geolocation is not supported in this browser.');
    }
  }, [clientId]);

  const handleGoogleLoginSuccess = (response) => {
    const userData = {
      name: response.profileObj.name,
      email: response.profileObj.email,
      profilePicture: response.profileObj.imageUrl,
      location: userLocation, // Add user's location here
    };

    history.push({
      pathname: '/LoginSucess',
      state: { user: userData },
    });
  };

  const handleGoogleLoginFailure = (response) => {
    console.log(response);
  };

  const handleFacebookLogin = (response) => {
    console.log(response);
  };

  const handleFacebookLoginFailure = (response) => {
    console.log(response);
  };

  const buttonStyle = {
    backgroundColor: 'rgb(215, 56, 55)',
    color: 'white',
    marginTop: '10px',
    padding: '10px 20px',
    borderRadius: '4px',
    fontWeight: 'bold',
    cursor: 'pointer',
    width: '100%',
  };

  return (
    <div>
      <div className="login-screen-content">
        <Container maxWidth="xs">
          <Grid container spacing={2} justifyContent="center" alignItems="center" className="login-form-container">
            <Grid item xs={12}>
              <div style={{ padding: '20px', borderRadius: '8px', boxShadow: '0px 2px 10px rgba(0, 0, 0, 0.1)' }}>
                <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px' }}>
                  {/* ... */}
                </div>
                <h2 style={{ textAlign: 'center' }}>Login</h2>
                <TextField label="Email" variant="outlined" fullWidth />
                <TextField label="Senha" type="password" variant="outlined" fullWidth style={{ marginTop: '10px' }} />
                <Button variant="contained" color="secondary" fullWidth style={{ ...buttonStyle, marginTop: '20px' }}>
                  Login
                </Button>
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '20px' }}>
                  {/* ... */}
                  <GoogleLogin
                    clientId="342699852295-18ukre2mehesbtmd2rjq82id2bhpqnob.apps.googleusercontent.com"
                    onSuccess={handleGoogleLoginSuccess}
                    onFailure={handleGoogleLoginFailure}
                    buttonText="Google"
                  />
                  {/* ... */}
                </div>
              </div>
            </Grid>
          </Grid>
        </Container>
      </div>
    </div>
  );
};

export default LoginScreen;
