import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Container, TextField, Button, Grid, AppBar, Toolbar } from '@mui/material';
import GoogleLogin from 'react-google-login';

const LoginScreen = () => {
  const [userInfo, setUserInfo] = useState({ name: '', email: '' });

  const handleGoogleLoginSuccess = (response) => {
    const { profileObj } = response;

    const userName = profileObj.name;
    const userEmail = profileObj.email;

    setUserInfo({ name: userName, email: userEmail });
    console.log('Google login success - Name:', userName, 'Email:', userEmail);
  };

  const handleGoogleLoginFailure = (error) => {
    console.log('Google login error:', error);
  };

  const buttonStyle = {
    backgroundColor: 'rgb(215, 56, 55)',
    color: 'white',
    marginTop: '10px',
    padding: '10px 20px',
    borderRadius: '4px',
    fontWeight: 'bold',
    cursor: 'pointer',
    width: '100%',
  };

  return (
    <div>
      <AppBar position="static" style={{ backgroundColor: 'rgb(215, 56, 55)', width: '100%' }}>
        <Toolbar />
      </AppBar>

      <Container maxWidth="xs">
        <Grid container spacing={2} justifyContent="center" alignItems="center" style={{ minHeight: '100vh' }}>
          <Grid item xs={12}>
            <div style={{ padding: '20px', borderRadius: '8px', boxShadow: '0px 2px 10px rgba(0, 0, 0, 0.1)' }}>
              {/* ... Seu código JSX existente ... */}

              {/* Renderizando as informações do usuário após o login */}
              {userInfo.name && (
                <div style={{ marginTop: '20px' }}>
                  <p>Nome: {userInfo.name}</p>
                  <p>E-mail: {userInfo.email}</p>
                </div>
              )}

              {/* ... Seu código JSX existente ... */}

              <GoogleLogin
                clientId="223407551839-83kqo80m0bollm1lv3ptddc91lp732hm.apps.googleusercontent.com"
                onSuccess={handleGoogleLoginSuccess}
                onFailure={handleGoogleLoginFailure}
                buttonText="Google"
                style={buttonStyle}
              />
            </div>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}

export default LoginScreen;
