  };

  const handleGoogleLoginFailure = (response) => {
    // ... your Google login failure logic ...
  };

  const handleFacebookLogin = (response) => {
    // ... your Facebook login success logic ...
  };

  const handleFacebookLoginFailure = (response) => {
    // ... your Facebook login failure logic ...
  };

  const handleLocationClick = () => {
    if ("geolocation" in navigator) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation({ latitude: position.coords.latitude, longitude: position.coords.longitude });
        },
        (error) => {
          console.error('Error getting location:', error);
        }
      );
    } else {
      console.error('Geolocation is not supported by this browser.');
    }
  };

  const buttonStyle = {
    backgroundColor: 'rgb(215, 56, 55)',
    color: 'white',
    marginTop: '10px',
    padding: '10px 20px',
    borderRadius: '4px',
    fontWeight: 'bold',
    cursor: 'pointer',
    width: '100%',
  };

  return (
    <div>
      <div className="login-screen-content">
        <Container maxWidth="xs">
          <Grid container spacing={2} justifyContent="center" alignItems="center" className="login-form-container">
            <Grid item xs={12}>
              <div style={{ padding: '20px', borderRadius: '8px', boxShadow: '0px 2px 10px rgba(0, 0, 0, 0.1)' }}>
                {/* ... rest of your JSX ... */}
                <Button variant="contained" color="primary" onClick={handleLocationClick}>
                  Get My Location
                </Button>
                {/* ... rest of your JSX ... */}
              </div>
            </Grid>
          </Grid>
        </Container>
      </div>
    </div>
  );
}

export default LoginScreen;
