import React, { useState } from 'react';
import { Container, TextField, Button, Grid, Typography } from '@mui/material';

const CadastroScreen = () => {
  const [step, setStep] = useState(1);

  const nextStep = () => {
    setStep(step + 1);
  };

  const prevStep = () => {
    setStep(step - 1);
  };

  return (
    <Container maxWidth="md">
      <Grid container spacing={0} justifyContent="flex-end" alignItems="center" style={{ height: '100vh' }}>
        <Grid item md={4}>
          <div style={{ padding: '20px', borderRadius: '8px', boxShadow: '0px 2px 10px rgba(0, 0, 0, 0.1)' }}>
            {step === 1 && (
              <div>
                <Typography variant="h4">Entrar com o email</Typography>
                <TextField label="Email" variant="outlined" fullWidth style={{ marginTop: '10px' }} />
                <Button variant="contained" color="secondary" fullWidth style={{ marginTop: '20px', backgroundColor: 'rgb(215, 56, 55)' }} onClick={nextStep}>
                  Próximo
                </Button>
              </div>
            )}
            {step === 2 && (
              <div>
                <Typography variant="h4">Inserir o celular</Typography>
                <TextField label="Celular" variant="outlined" fullWidth style={{ marginTop: '10px' }} />
                <div style={{ marginTop: '20px' }}>
                  <Button variant="outlined" color="secondary" onClick={prevStep}>
                    Voltar
                  </Button>
                  <Button variant="contained" color="secondary" style={{ marginLeft: '10px', backgroundColor: 'rgb(215, 56, 55)' }} onClick={nextStep}>
                    Próximo
                  </Button>
                </div>
              </div>
            )}
            {/* Repita o padrão para os outros passos */}
            {/* Passo 3, Passo 4, ... */}
            {step > 2 && step < 8 && (
              <div>
                <Typography variant="h4">Passo {step - 1}</Typography>
                {/* Coloque o campo correspondente aqui */}
                <div style={{ marginTop: '20px' }}>
                  <Button variant="outlined" color="secondary" onClick={prevStep}>
                    Voltar
                  </Button>
                  <Button variant="contained" color="secondary" style={{ marginLeft: '10px', backgroundColor: 'rgb(215, 56, 55)' }} onClick={nextStep}>
                    Próximo
                  </Button>
                </div>
              </div>
            )}
            {step === 8 && (
              <div>
                <Typography variant="h4">Colocar complemento</Typography>
                <TextField label="Complemento" variant="outlined" fullWidth style={{ marginTop: '10px' }} />
                <div style={{ marginTop: '20px' }}>
                  <Button variant="outlined" color="secondary" onClick={prevStep}>
                    Voltar
                  </Button>
                  <Button variant="contained" color="secondary" style={{ marginLeft: '10px', backgroundColor: 'rgb(215, 56, 55)' }}>
                    Cadastrar
                  </Button>
                </div>
              </div>
            )}
          </div>
        </Grid>
      </Grid>
    </Container>
  );
}

export default CadastroScreen;
