import React, { useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { Container, TextField, Button, Grid, AppBar, Toolbar } from '@mui/material';
import GoogleLogin from 'react-google-login';
import FacebookLogin from 'react-facebook-login';
import { gapi } from 'gapi-script';
import './LoginScreen.css';

const LoginScreen = () => {
  const history = useHistory();
  const clientId = '223407551839-u5p661d3dn6noi04668sf1aude820lqe.apps.googleusercontent.com';
  useEffect(() => {
    gapi.load('auth2', () => {
      gapi.auth2.init({ client_id: clientId });
    });
  }, [clientId]);

  const handleGoogleLoginSuccess = (response) => {
    console.log(response);

    const userData = {
      name: response.profileObj.name,
      email: response.profileObj.email,
      profilePicture: response.profileObj.imageUrl,
    };

    history.push({
      pathname: '/LoginSucess',
      state: { user: userData },
    });
  };

  const handleGoogleLoginFailure = (response) => {
    console.log(response);
  };

  const handleFacebookLogin = (response) => {
    console.log(response);
  };

  const handleFacebookLoginFailure = (response) => {
    console.log(response);
  };

  return (
    <div className="login-screen-container">
      <AppBar position="static" className="app-bar">
        <Toolbar />
      </AppBar>

      <Container maxWidth="sm" className="login-form-container">
        <h2>Login</h2>
        <TextField label="Email" variant="outlined" fullWidth />
        <TextField label="Password" type="password" variant="outlined" fullWidth style={{ marginTop: '10px' }} />
        <Button variant="contained" color="secondary" fullWidth className="login-button">
          Login
        </Button>
        <Link to="/cadastro" className="create-account-button">
          Create an Account
        </Link>
        <div className="social-buttons">
          <GoogleLogin
            clientId="223407551839-u5p661d3dn6noi04668sf1aude820lqe.apps.googleusercontent.com"
            onSuccess={handleGoogleLoginSuccess}
            onFailure={handleGoogleLoginFailure}
            buttonText="Google"
            className="social-button google-login-button"
          />
          <FacebookLogin
            appId="13fb2f1e94f7c31e7746297793fd5ac6"
            onSuccess={handleFacebookLogin}
            onFailure={handleFacebookLoginFailure}
            textButton="Facebook"
            cssClass="social-button facebook-login-button"
          />
        </div>
      </Container>
    </div>
  );
};

export default LoginScreen;
