import React, { useState } from 'react';
import { Container, TextField, Button, Grid, Typography } from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';

const CadastroScreen = () => {
  const [step, setStep] = useState(1);

  const nextStep = () => {
    if (step < 9) {
      setStep(step + 1);
    }
  };

  const prevStep = () => {
    setStep(step - 1);
  };

  const getStepTitle = () => {
    switch (step) {
      case 1:
        return 'Entrar com o email';
      case 2:
        return 'Inserir o celular';
      case 3:
        return 'Código de validação';
      case 4:
        return 'Inserir nome completo';
      case 5:
        return 'Inserir CPF';
      case 6:
        return 'Botão cadastrar';
      case 7:
        return 'Inserir endereço';
      case 8:
        return 'Localizar endereço no mapa';
      case 9:
        return 'Colocar complemento';
      default:
        return '';
    }
  };

  return (
    <Container maxWidth="md">
      <Grid container spacing={0} justifyContent="flex-end" alignItems="center" style={{ height: '100vh' }}>
        <Grid item md={4}>
          <div style={{ padding: '20px', borderRadius: '8px', boxShadow: '0px 2px 10px rgba(0, 0, 0, 0.1)' }}>
            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px' }}>
              <img src={`/svg/step${step}.svg`} alt={`Step ${step} Icon`} width="50" height="50" />
            </div>
            <Typography variant="h5" style={{ marginBottom: '10px' }}>{getStepTitle()}</Typography>
            {step === 1 && (
              <div>
                <TextField label="Email" variant="outlined" fullWidth style={{ marginTop: '10px' }} />
              </div>
            )}
            {step === 2 && (
              <div>
                <TextField label="Celular" variant="outlined" fullWidth style={{ marginTop: '10px' }} />
              </div>
            )}
            {step === 3 && (
              <div>
                <TextField label="Código de Validação" variant="outlined" fullWidth style={{ marginTop: '10px' }} />
              </div>
            )}
            {step === 4 && (
              <div>
                <TextField label="Nome Completo" variant="outlined" fullWidth style={{ marginTop: '10px' }} />
              </div>
            )}
            {step === 5 && (
              <div>
                <TextField label="CPF" variant="outlined" fullWidth style={{ marginTop: '10px' }} />
              </div>
            )}
            {step === 6 && (
              <div>
                <Button variant="contained" color="secondary" fullWidth style={{ marginTop: '20px', backgroundColor: 'rgb(215, 56, 55)' }}>
                  Cadastrar
                </Button>
              </div>
            )}
            {step === 7 && (
              <div>
                <TextField label="Endereço" variant="outlined" fullWidth style={{ marginTop: '10px' }} />
              </div>
            )}
            {step === 8 && (
              <div>
                {/* Coloque o mapa aqui */}
              </div>
            )}
            {step === 9 && (
              <div>
                <TextField label="Complemento" variant="outlined" fullWidth style={{ marginTop: '10px' }} />
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '20px' }}>
                  <ArrowBackIcon color="secondary" onClick={prevStep} />
                  <Button variant="contained" color="secondary" style={{ backgroundColor: 'rgb(215, 56, 55)' }}>
                    Adicionar complemento
                  </Button>
                </div>
              </div>
            )}
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '20px' }}>
              {step !== 1 && (
                <ArrowBackIcon color="secondary" onClick={prevStep} />
              )}
              {step !== 9 && (
                <Button variant="contained" color="secondary" style={{ backgroundColor: 'rgb(215, 56, 55)' }} onClick={nextStep}>
                  Próximo
                </Button>
              )}
            </div>
          </div>
        </Grid>
      </Grid>
    </Container>
  );
}

export default CadastroScreen;
