import React, { useEffect } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { Container, TextField, Button, Grid } from '@mui/material';
import GoogleLogin from 'react-google-login';
import FacebookLogin from 'react-facebook-login';
import { gapi } from 'gapi-script';
import './LoginScreen.css';

const LoginScreen = () => {
  const history = useHistory();
  const clientId = 'YOUR_GOOGLE_CLIENT_ID';

  // Rest of the component code, including useEffect and login handlers

  return (
    <div className="login-screen-container">
      <Container maxWidth="sm" className="login-form-container">
        <h2 className="login-heading">Welcome Back</h2>
        <form className="login-form">
          <TextField label="Email" variant="outlined" fullWidth />
          <TextField label="Password" type="password" variant="outlined" fullWidth className="password-field" />
          <Button variant="contained" color="primary" fullWidth className="login-button">
            Login
          </Button>
          <Link to="/cadastro" className="create-account-link">
            Create an Account
          </Link>
        </form>
        <div className="social-buttons">
          <GoogleLogin
            clientId={clientId}
            onSuccess={handleGoogleLoginSuccess}
            onFailure={handleGoogleLoginFailure}
            buttonText="Google"
            className="social-button google-login-button"
          />
          <FacebookLogin
            appId="YOUR_FACEBOOK_APP_ID"
            onSuccess={handleFacebookLogin}
            onFailure={handleFacebookLoginFailure}
            textButton="Facebook"
            cssClass="social-button facebook-login-button"
          />
        </div>
      </Container>
    </div>
  );
};

export default LoginScreen;
