import React from 'react';
import { Container, TextField, Button, Grid, AppBar, Toolbar } from '@mui/material';
import GoogleLogin from 'react-google-login';

const LoginScreen = () => {
  // Função para lidar com o sucesso do login do Google
  const handleGoogleLoginSuccess = (response) => {
    console.log('Google login success:', response);
  };

  // Função para lidar com o erro do login do Google
  const handleGoogleLoginFailure = (error) => {
    console.log('Google login error:', error);
  };

  return (
    <div>
      {/* Faixa no topo da página */}
      <AppBar position="static" style={{ backgroundColor: 'rgb(215, 56, 55)', width: '100%' }}>
        <Toolbar />
      </AppBar>

      {/* Conteúdo da tela de login */}
      <Container maxWidth="xs">
        <Grid container spacing={2} justifyContent="center" alignItems="center" style={{ minHeight: '100vh' }}>
          <Grid item xs={12}>
            <div style={{ padding: '20px', borderRadius: '8px', boxShadow: '0px 2px 10px rgba(0, 0, 0, 0.1)' }}>
              <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: '20px' }}>
                {/* Adicione aqui o seu logo */}
                <img src="/path/to/logo.svg" alt="Logo" width="100" />
              </div>
              <h2 style={{ textAlign: 'center' }}>Login</h2>
              <TextField label="Email" variant="outlined" fullWidth />
              <TextField label="Senha" type="password" variant="outlined" fullWidth style={{ marginTop: '10px' }} />
              <Button variant="contained" color="secondary" fullWidth style={{ marginTop: '20px', backgroundColor: 'rgb(215, 56, 55)' }}>
                Login
              </Button>
              {/* Botão de login do Google */}
              <GoogleLogin
                clientId="SEU_CLIENT_ID_DO_GOOGLE"
                onSuccess={handleGoogleLoginSuccess}
                onFailure={handleGoogleLoginFailure}
                buttonText="Login com Google"
              />
              {/* Botão de login do Facebook */}
              <FacebookLogin
                appId="SEU_APP_ID_DO_FACEBOOK"
                fields="name,email,picture"
                callback={handleFacebookLogin}
                textButton="Login com Facebook"
              />
            </div>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}

export default LoginScreen;
