import React from 'react';
import { Link } from 'react-router-dom';
import { Container, TextField, Button, Grid, AppBar, Toolbar } from '@mui/material';
import firebase from 'firebase/app';
import 'firebase/auth'; // Importe os módulos necessários do Firebase
import { FirebaseAuthProvider, FirebaseAuthConsumer } from '@react-firebase/auth'; // Importe os componentes do pacote react-firebase/auth

// Configuração do Firebase (coloque suas próprias configurações aqui)
const firebaseConfig = {
  apiKey: 'YOUR_API_KEY',
  authDomain: 'YOUR_AUTH_DOMAIN',
  projectId: 'YOUR_PROJECT_ID',
  storageBucket: 'YOUR_STORAGE_BUCKET',
  messagingSenderId: 'YOUR_MESSAGING_SENDER_ID',
  appId: 'YOUR_APP_ID',
};

const LoginScreen = () => {

  const buttonStyle = {
    backgroundColor: 'rgb(215, 56, 55)',
    color: 'white',
    marginTop: '10px',
    padding: '10px 20px',
    borderRadius: '4px',
    fontWeight: 'bold',
    cursor: 'pointer',
    width: '100%',
  };

  return (
    <FirebaseAuthProvider {...firebaseConfig} firebase={firebase}>
      <div>
        <AppBar position="static" style={{ backgroundColor: 'rgb(215, 56, 55)', width: '100%' }}>
          <Toolbar />
        </AppBar>

        <Container maxWidth="xs">
          <Grid container spacing={2} justifyContent="center" alignItems="center" style={{ minHeight: '100vh' }}>
            <Grid item xs={12}>
              <div style={{ padding: '20px', borderRadius: '8px', boxShadow: '0px 2px 10px rgba(0, 0, 0, 0.1)' }}>
                <h2 style={{ textAlign: 'center' }}>Login</h2>
                <TextField label="Email" variant="outlined" fullWidth />
                <TextField label="Senha" type="password" variant="outlined" fullWidth style={{ marginTop: '10px' }} />
                <Button variant="contained" color="secondary" fullWidth style={{ ...buttonStyle, marginTop: '20px' }}>
                  Login
                </Button>
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '20px' }}>
                  <Link to="/cadastro" style={{ textDecoration: 'none', marginBottom: '10px', width: '100%' }}>
                    <Button variant="contained" color="secondary" style={buttonStyle}>
                      Criar uma conta
                    </Button>
                  </Link>
                  {/* Use o FirebaseAuthConsumer para integrar a autenticação do Google */}
                  <FirebaseAuthConsumer>
                    {({ isSignedIn, user, providerId }) =>
                      !isSignedIn ? (
                        <Button
                          variant="contained"
                          color="secondary"
                          style={buttonStyle}
                          onClick={() => {
                            const googleAuthProvider = new firebase.auth.GoogleAuthProvider();
                            firebase.auth().signInWithPopup(googleAuthProvider);
                          }}
                        >
                          Login com Google
                        </Button>
                      ) : (
                        <p>Usuário já autenticado: {user.displayName}</p>
                      )
                    }
                  </FirebaseAuthConsumer>
                </div>
              </div>
            </Grid>
          </Grid>
        </Container>
      </div>
    </FirebaseAuthProvider>
  );
};

export default LoginScreen;
